index.css:
.control-section.badge-samples {
    overflow: auto;
}

.sample_container.badge-accordion {
    max-width: 350px;
    margin: auto;
    margin-top: 20px;
}

/* Font Icons */

@font-face {
    font-family: 'Contacts';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.badge-accordion .people,
.badge-accordion .e-people {
    font-family: 'Contacts';
}

.e-people:before {
    content: '\e700';
}

.people:before {
    content: '\e701';
}

/* Accordion Customization */

#accordion .e-acrdn-header .e-toggle-icon .e-tgl-collapse-icon.e-icons {
    display: none;
}

.e-acrdn-icons:not(.e-icons) {
    padding: 0 8px 0 0;
    vertical-align: middle;
}

.sample_container.badge-accordion li.msg {
    line-height: 36px;
    list-style-type: none;
    text-indent: 16px;
}

.accordion-control-section {
    margin: 0 10% 0 10%;
}

.sample_container.badge-accordion #accordion .e-toggle-icon {
    font-size: 18px;
    top: -1px;
    display: block;
}

