index.css:
.radiobutton-control {
    margin: 5% 0 5% 40%;
}
/* custom code start */
@media only screen and (max-width: 700px) {
    .radiobutton-control {
        margin-left: 35%;
        margin-top: 8%;
    }

    .control-section {
        min-height: 200px;
    }
}

@media only screen and (max-width: 500px) {
    .radiobutton-control {
        margin-top: 20%;
        margin-left: 21%;
        margin-bottom: 27%;
    }
}

.control-section .row {
    margin: 20px 0;
}

.radiobutton-control h4 {
    font-size: 18px;
}

.e-bigger .radiobutton-control h4 {
    font-size: 20px;
}

/* custom code end */

index.js:
import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { RadioButtonComponent } from '@syncfusion/ej2-react-buttons';
import { SampleBase } from './sample-base';

export class RadioButton extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <div className='control-section'>
                    <div className='radiobutton-control'>
                        <h4>Select a payment method</h4>
                        <div className='row'>
                            <RadioButtonComponent checked={true} label='Credit/Debit card' name='payment' value="credit/debit"></RadioButtonComponent>
                        </div>
                        <div className='row'>
                            <RadioButtonComponent label='Net Banking' name='payment' value="netbanking"></RadioButtonComponent>
                        </div>
                        <div className='row'>
                            <RadioButtonComponent label='Cash on Delivery' name='payment' value="cashondelivery"></RadioButtonComponent>
                        </div>
                        <div className='row'>
                            <RadioButtonComponent label='Other Wallets' name='payment' value="others"></RadioButtonComponent>
                        </div>
                    </div>
                </div>
            </div>);
    }
}

render(<RadioButton />, document.getElementById('sample'));

