import { render } from 'react-dom';
import './index.css';
/**
 * Sample for Empty Point
 */
import * as React from "react";
import { ChartComponent, SeriesCollectionDirective, SeriesDirective, ColumnSeries, SplineSeries, AreaSeries, Category, Legend, Tooltip, Inject } from '@syncfusion/ej2-react-charts';
import { PropertyPane } from './property-pane';
import { DropDownListComponent } from '@syncfusion/ej2-react-dropdowns';
import { SampleBase } from './sample-base';
export let data1 = [
    { x: 'Rice', y: 80 }, { x: 'Wheat', y: null }, { x: 'Oil', y: 70 },
    { x: 'Corn', y: 60 }, { x: 'Gram', y: null },
    { x: 'Milk', y: 70 }, { x: 'Peas', y: 80 },
    { x: 'Fruit', y: 60 }, { x: 'Butter', y: null }
];
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
	}`;
export class EmptyPoint extends SampleBase {
    constructor() {
        super(...arguments);
        this.droplist = [
            { value: 'Column' },
            { value: 'Area' },
            { value: 'Spline' },
        ];
        this.modelist = [
            { value: 'Gap' },
            { value: 'Drop' },
            { value: 'Average' },
            { value: 'Zero' }
        ];
    }
    change() {
        this.chartInstance.series[0].type = this.dropElement.value;
        this.chartInstance.refresh();
    }
    ;
    mode() {
        this.chartInstance.series[0].emptyPointSettings.mode = this.modeElement.value;
        this.chartInstance.series[0].emptyPointSettings.fill = '#e6e6e6';
        this.chartInstance.refresh();
    }
    ;
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section row'>
                    <div className='col-md-8'>
                        <ChartComponent id='charts' ref={chart => this.chartInstance = chart} primaryXAxis={{
            title: 'Product', valueType: 'Category', interval: 1
        }} chartArea={{ border: { width: 0 } }} primaryYAxis={{
            title: 'Profit', minimum: 0, maximum: 100, interval: 20, labelFormat: '{value}%'
        }} load={this.load.bind(this)} legendSettings={{ visible: false }} title="Annual Product-Wise Profit Analysis" loaded={this.onChartLoad.bind(this)} tooltip={{ enable: true }}>
                            <Inject services={[ColumnSeries, Category, Legend, Tooltip, SplineSeries, AreaSeries]}/>
                            <SeriesCollectionDirective>
                                <SeriesDirective dataSource={data1} xName='x' yName='y' width={2} name='Profit' type='Column' marker={{ visible: true, height: 10, width: 10 }} emptyPointSettings={{
            fill: '#e6e6e6'
        }}>
                                </SeriesDirective>
                            </SeriesCollectionDirective>
                        </ChartComponent>
                    </div>
                    <div className='col-md-4 property-section'>
                        <PropertyPane title='Properties'>
                            <table id='property' title='Properties' className='property-panel-table' style={{ width: '100%' }}>
                                <tr style={{ height: '50px' }}>
                                    <td style={{ width: '60%' }}>
                                        <div>Series Type: </div>
                                    </td>
                                    <td style={{ width: '40%' }}>
                                        <div>
                                            <DropDownListComponent width="120px" id="selmode" change={this.change.bind(this)} ref={d => this.dropElement = d} dataSource={this.droplist} fields={{ text: 'value', value: 'value' }} value="Column"/>
                                        </div>
                                    </td>
                                </tr>
                                <tr style={{ height: '50px' }}>
                                    <td style={{ width: '60%' }}>
                                        <div>Empty Point Mode: </div></td>
                                    <td style={{ width: '40%' }}>
                                        <div>
                                            <DropDownListComponent width="120px" id="selmode" change={this.mode.bind(this)} ref={d => this.modeElement = d} dataSource={this.modelist} fields={{ text: 'value', value: 'value' }} value="Gap"/>
                                        </div>
                                    </td>
                                </tr>
                            </table>
                        </PropertyPane>
                    </div>
                </div>
            </div>);
    }
    onChartLoad(args) {
        document.getElementById('charts').setAttribute('title', '');
    }
    ;
}

render(<EmptyPoint />, document.getElementById('sample'));

