import { render } from 'react-dom';
import './index.css';
/**
 * Sample for Stacking Column series
 */
import * as React from "react";
import { ChartComponent, SeriesCollectionDirective, SeriesDirective, Inject, Legend, Category, StackingColumnSeries, Tooltip } from '@syncfusion/ej2-react-charts';
import { SampleBase } from './sample-base';
import { Browser } from '@syncfusion/ej2-base';
export let data1 = [
    { x: '2014', y: 111.1 },
    { x: '2015', y: 127.3 },
    { x: '2016', y: 143.4 },
    { x: '2017', y: 159.9 }
];
export let data2 = [
    { x: '2014', y: 76.9 },
    { x: '2015', y: 99.5 },
    { x: '2016', y: 121.7 },
    { x: '2017', y: 142.5 }
];
export let data3 = [
    { x: '2014', y: 66.1 },
    { x: '2015', y: 79.3 },
    { x: '2016', y: 91.3 },
    { x: '2017', y: 102.4 }
];
export let data4 = [
    { x: '2014', y: 34.1 },
    { x: '2015', y: 38.2 },
    { x: '2016', y: 44.0 },
    { x: '2017', y: 51.6 }
];
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
    }`;
export class StackedColumn extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section'>
                    <ChartComponent id='charts' style={{ textAlign: "center" }} primaryXAxis={{
            majorGridLines: { width: 0 },
            minorGridLines: { width: 0 },
            majorTickLines: { width: 0 },
            minorTickLines: { width: 0 },
            interval: 1,
            lineStyle: { width: 0 },
            labelIntersectAction: 'Rotate45',
            valueType: 'Category'
        }} primaryYAxis={{
            title: 'Sales',
            lineStyle: { width: 0 },
            minimum: 0,
            maximum: 500,
            interval: 100,
            majorTickLines: { width: 0 },
            majorGridLines: { width: 1 },
            minorGridLines: { width: 1 },
            minorTickLines: { width: 0 },
            labelFormat: '{value}B',
        }} width={Browser.isDevice ? '100%' : '60%'} chartArea={{ border: { width: 0 } }} load={this.load.bind(this)} title='Mobile Game Market by Country' loaded={this.onChartLoad.bind(this)} tooltip={{ enable: true }}>
                        <Inject services={[StackingColumnSeries, Category, Legend, Tooltip]}/>
                        <SeriesCollectionDirective>
                            <SeriesDirective dataSource={data1} xName='x' yName='y' name='UK' type='StackingColumn'>
                            </SeriesDirective>
                            <SeriesDirective dataSource={data2} xName='x' yName='y' name='Germany' type='StackingColumn'>
                            </SeriesDirective>
                            <SeriesDirective dataSource={data3} xName='x' yName='y' name='France' type='StackingColumn'>
                            </SeriesDirective>
                            <SeriesDirective dataSource={data4} xName='x' yName='y' name='Italy' type='StackingColumn'>
                            </SeriesDirective>
                        </SeriesCollectionDirective>
                    </ChartComponent>
                    <div style={{ float: 'right', marginRight: '10px' }}>Source: &nbsp;
                         <a href="https://www.cyberagent.co.jp/en/newsinfo/press/detail/id=12026" target="_blank">www.cyberagent.co.jp</a>
                    </div>
                </div>
            </div>);
    }
    onChartLoad(args) {
        let chart = document.getElementById('charts');
        chart.setAttribute('title', '');
    }
    ;
}

render(<StackedColumn />, document.getElementById('sample'));

