import { render } from 'react-dom';
import './index.css';
/**
 * Sample for Step line series
 */
import * as React from "react";
import { ChartComponent, SeriesCollectionDirective, SeriesDirective, Inject, Legend, DateTime, StepLineSeries, Tooltip } from '@syncfusion/ej2-react-charts';
import { SampleBase } from './sample-base';
import { Browser } from '@syncfusion/ej2-base';
/**
 * StepLine Series
 */
export let data1 = [
    { x: new Date(1975, 0, 1), y: 16 },
    { x: new Date(1980, 0, 1), y: 12.5 },
    { x: new Date(1985, 0, 1), y: 19 },
    { x: new Date(1990, 0, 1), y: 14.4 },
    { x: new Date(1995, 0, 1), y: 11.5 },
    { x: new Date(2000, 0, 1), y: 14 },
    { x: new Date(2005, 0, 1), y: 10 },
    { x: new Date(2010, 0, 1), y: 16 }
];
export let data2 = [
    { x: new Date(1975, 0, 1), y: 10 },
    { x: new Date(1980, 0, 1), y: 7.5 },
    { x: new Date(1985, 0, 1), y: 11 },
    { x: new Date(1990, 0, 1), y: 7 },
    { x: new Date(1995, 0, 1), y: 8 },
    { x: new Date(2000, 0, 1), y: 6 },
    { x: new Date(2005, 0, 1), y: 3.5 },
    { x: new Date(2010, 0, 1), y: 7 }
];
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
    }`;
export class StepLine extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section'>
                    <ChartComponent id='charts' style={{ textAlign: "center" }} primaryXAxis={{
            labelFormat: 'y',
            intervalType: 'Years',
            majorGridLines: { width: 0 },
            valueType: 'DateTime',
            edgeLabelPlacement: 'Shift'
        }} load={this.load.bind(this)} primaryYAxis={{
            lineStyle: { width: 0 },
            interval: 5,
            majorTickLines: { width: 0 },
            labelFormat: '{value}%'
        }} width={Browser.isDevice ? '100%' : '60%'} chartArea={{ border: { width: 0 } }} tooltip={{ enable: true }} loaded={this.onChartLoad.bind(this)} title='Unemployment Rates 1975-2010'>
                        <Inject services={[StepLineSeries, Legend, Tooltip, DateTime]}/>
                        <SeriesCollectionDirective>
                            <SeriesDirective dataSource={data1} xName='x' yName='y' name='China' width={2} type='StepLine' marker={{ visible: true, width: 10, height: 10 }}>
                            </SeriesDirective>
                            <SeriesDirective dataSource={data2} xName='x' yName='y' name='Australia' width={2} type='StepLine' marker={{ visible: true, width: 10, height: 10 }}>
                            </SeriesDirective>
                        </SeriesCollectionDirective>
                    </ChartComponent>
                </div>
            </div>);
    }
    onChartLoad(args) {
        let chart = document.getElementById('charts');
        chart.setAttribute('title', '');
    }
    ;
}

render(<StepLine />, document.getElementById('sample'));

