index.css:
/* Common sample level styles */
#inline-control {
    width: 100%;
    text-align: center;
}
#inline-control .col-xs-12 {
    margin: 10px 0;
}
#inline-control h4 {
    margin-bottom: 20px;
}

.e-bigger #inline-control h4 {
    font-size: 20px;
}
#inline-control.e-mobile-control h4 {
    margin-bottom: 15px;
}

/* Preview area styles */
#inline-control #preview {
    background: transparent url('////npmci.syncfusion.com/development/react/demos/src/color-picker/images/pen.png') no-repeat;
    display: inline-block;
    height: 80px;
    margin: 10px 0;
    width: 600px;
    background-color: #008000;
}
#inline-control.e-mobile-control #preview {
    background: transparent url('////npmci.syncfusion.com/development/react/demos/src/color-picker/images/pen_mobile.png') no-repeat;
    background-color: #008000;
    height: 35px;
    width: 300px;
}
.highcontrast #inline-control #preview {
    background: transparent url('////npmci.syncfusion.com/development/react/demos/src/color-picker/images/pen_highcontrast.png') no-repeat;
    background-color: #008000;
}
.highcontrast #inline-control.e-mobile-control #preview {
    background: transparent url('////npmci.syncfusion.com/development/react/demos/src/color-picker/images/pen_mobile_highcontrast.png') no-repeat;
    background-color: #008000;
}

/* Common sample level styles for mobile */
#inline-control.e-mobile-control #inline-content {
    display: flex;
    flex-wrap: nowrap;
    overflow-x: auto;
}
#inline-control.e-mobile-control #inline-content .col-xs-12 {
    flex: 0 0 auto;
    margin-top: 5px;
}

index.js:
import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { ColorPickerComponent } from '@syncfusion/ej2-react-inputs';
import { Browser } from '@syncfusion/ej2-base';
import { SampleBase } from './sample-base';

export class Inline extends SampleBase {
    // function to handle the ColorPicker change event
    change(args) {
        document.getElementById('preview').style.backgroundColor = args.currentValue.hex;
    }
    rendereComplete() {
        if (Browser.isDevice) {
            document.getElementById('inline-control').classList.add('e-mobile-control');
        }
    }
    render() {
        return (<div className='control-pane'>
        <div className='inline control-section'>
          <div id='inline-control'>
            <div className='row'>
              <div id='preview'></div>
            </div>
            <div id='inline-content' className='row'>
              <div className='col-xs-12 col-sm-12 col-lg-6 col-md-6'>
                <h4>Inline Palette</h4>
                <ColorPickerComponent id='inline-palette' mode='Palette' modeSwitcher={false} inline={true} showButtons={false} change={this.change.bind(this)}></ColorPickerComponent>
              </div>
              <div className='col-xs-12 col-sm-12 col-lg-6 col-md-6'>
                <h4>Inline Picker</h4>
                <ColorPickerComponent id='inline-picker' mode='Picker' modeSwitcher={false} inline={true} showButtons={false} change={this.change.bind(this)}></ColorPickerComponent>
              </div>
            </div>
          </div>
        </div>


      </div>);
    }
}

render(<Inline />, document.getElementById('sample'));

