index.css:
.control-section .dual-list-wrapper {
    width: 60%;
    margin: 0 auto 10px;
    display: flex;
}
.dual-list-groupa,
.dual-list-groupb {
    width: 50%;
}
.dual-list-groupb .e-listbox-wrapper,
.dual-list-groupb h4 {
    width: 87%;
    margin-left: 15px;
}
@media screen and (max-width: 590px) {
    .control-section .dual-list-wrapper {
        width: 100%;
    }
    .dual-list-groupa {
        width: 56%;
    }
    .dual-list-groupb {
        width: 44%;
    }
}
@media screen and (max-width: 400px) {
    .dual-list-groupa {
        width: 59%;
    }
    .dual-list-groupb {
        width: 41%;
    }
}

index.js:
import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { ListBoxComponent } from '@syncfusion/ej2-react-dropdowns';
import { SampleBase } from './sample-base';
import * as data from './dataSource.json';

export class DualListBox extends SampleBase {
    constructor() {
        super(...arguments);
        this.dataA = data["groupa"];
        this.dataB = data["groupb"];
        this.fields = { text: 'Name' };
        this.toolbarSettings = { items: ['moveUp', 'moveDown', 'moveTo', 'moveFrom', 'moveAllTo', 'moveAllFrom'] };
    }
    render() {
        return (<div className='control-pane'>
                <div className="control-section">
                    <div className="dual-list-wrapper">
                        <div className="dual-list-groupa">
                            <h4>Group A</h4>
                            <ListBoxComponent dataSource={this.dataA} fields={this.fields} height="330px" scope="#combined-listbox" toolbarSettings={this.toolbarSettings}/>
                        </div>
                        <div className="dual-list-groupb">
                            <h4>Group B</h4>
                            <ListBoxComponent id="combined-listbox" dataSource={this.dataB} height="330px" fields={this.fields}/>
                        </div>
                    </div>
                </div>
            </div>);
    }
}

render(<DualListBox />, document.getElementById('sample'));

