import { render } from 'react-dom';
import './index.css';
/**
 * Projection sample
 */
import * as React from "react";
import { MapAjax } from '@syncfusion/ej2-maps';
import { MapsComponent, Inject, LayersDirective, LayerDirective, Zoom, Marker, MarkersDirective, MarkerDirective } from '@syncfusion/ej2-react-maps';
import { SampleBase } from './sample-base';
const SAMPLE_CSS = `
.pulse-css {
    width: 20px;
    height: 20px;
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    border-radius: 20px;
    background:#E94430;
    position: relative;
  }
  .pulse-css:before, .pulse-css:after {
    content: '';
    width: 20px;
    height: 20px;
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    border-radius: 20px;
    background-color: #E94430;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    margin: auto;
    transform: scale(0.5);
    transform-origin: center center;
    animation: pulse-me 3s linear infinite;
  }
  .pulse-css:after {
    animation-delay: 2s;
  }
  @keyframes pulse-me {
    0% {
      transform: scale(0.5);
      opacity: 0;
    }
    50% {
      opacity: 0.3;
    }
    70% {
      opacity: 0.1;
    }
    100% {
      transform: scale(5);
      opacity: 0;
    }
  }
  
    `;
let marketTemp = '<div id="template"> <div class="pulse-container"><div class="pulse-box"><div class="pulse-css"></div></div></div></div>';
export class EarthquakeMaps extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section row'>
                    <div className='col-md-12'>
                        <MapsComponent id="maps" loaded={this.onMapsLoad.bind(this)} load={this.load} ref={m => this.mapInstance = m} zoomSettings={{
            enable: false,
            zoomFactor: 7
        }} mapsArea={{
            background: '#AEE2FA'
        }} titleSettings={{
            text: '7.6 Magnitude earthquake strikes Sumatra - 2009',
            textStyle: {
                size: '18px'
            }
        }} centerPosition={{
            latitude: 1.5053645409602877,
            longitude: 105.14038085937499
        }}>
                            <Inject services={[Zoom, Marker]}/>
                            <LayersDirective>
                                <LayerDirective shapeData={new MapAjax('////npmci.syncfusion.com/development/react/demos/src/maps/map-data/asia.json')} shapePropertyPath='name' shapeDataPath='name' shapeSettings={{
            fill: '#FFFDCF',
            border: {
                color: '#3497C3',
                width: 0.5
            }
        }}>
                                    <MarkersDirective>
                                        <MarkerDirective visible={true} height={100} width={100} animationDuration={0} template={marketTemp} dataSource={[{
                latitude: 1.625758360412755, longitude: 106.5693359375
            }]}>
                                        </MarkerDirective>
                                    </MarkersDirective>
                                </LayerDirective>
                            </LayersDirective>
                        </MapsComponent>
                    </div>
                    
                    <div style={{ float: 'right', marginright: '10px' }}>Source: 
                        <a href="https://en.wikipedia.org/wiki/2009_Sumatra_earthquakes" target="_blank">en.wikipedia.org</a>
                    </div>
                </div>
            </div>);
    }
    onMapsLoad(args) {
        let maps = document.getElementById('maps');
        maps.setAttribute('title', '');
    }
    ;
}

render(<EarthquakeMaps />, document.getElementById('sample'));

