import { render } from 'react-dom';
import './index.css';
/**
 * Projection sample
 */
import * as React from "react";
import { MapAjax } from '@syncfusion/ej2-maps';
import { MapsComponent, Inject, LayersDirective, LayerDirective, MapsTooltip, Legend, Marker } from '@syncfusion/ej2-react-maps';
import { SampleBase } from './sample-base';
import * as data from './map-data/heatmap-datasource.json';
let datasource = data;
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
    }`;
let colormapping = [{
        from: 60000, to: 400000, color: '#9fdfdf', label: '<0.4M'
    },
    {
        from: 400000, to: 10000000, color: '#79d2d2', label: '0.4-10M'
    },
    {
        from: 10000000, to: 20000000, color: '#53C6C6', label: '10-20M'
    },
    {
        from: 20000000, to: 70000000, color: '#39acac', label: '20-70M'
    },
    {
        from: 70000000, to: 100000000, color: '#339999', label: '70-100M'
    },
    {
        from: 100000000, to: 200000000, color: '#2d8686', label: '>100M'
    }
];
export class HeatMaps extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                    
                </style>
                <div className='control-section row'>
                    <div className='col-md-12'>
                        <MapsComponent id="maps" loaded={this.onMapsLoad.bind(this)} load={this.load} ref={m => this.mapInstance = m} useGroupingSeparator={true} format={"n"} zoomSettings={{
            enable: false
        }} legendSettings={{
            visible: true,
            mode: 'Interactive',
            position: 'Bottom',
            height: '10',
            width: '350',
            alignment: 'Center',
            labelDisplayMode: 'Trim'
        }} titleSettings={{
            text: "State wise India's population - 2011",
            textStyle: {
                size: '16px'
            }
        }}>
                            <Inject services={[Marker, MapsTooltip, Legend]}/>
                            <LayersDirective>
                                <LayerDirective shapeData={new MapAjax('////npmci.syncfusion.com/development/react/demos/src/maps/map-data/india.json')} shapePropertyPath='NAME_1' shapeDataPath='Name' dataSource={datasource.heatmap} tooltipSettings={{
            visible: true,
            valuePath: 'population',
            format: 'State: ${Name} <br> Population: ${population}'
        }} shapeSettings={{
            /*border: {
                width: 0.2,
                color: 'white'
            },*/
            colorValuePath: 'population',
            colorMapping: colormapping
        }}>
                                </LayerDirective>
                            </LayersDirective>
                        </MapsComponent>
                    </div>
                </div>
                
                <div style={{ float: 'right', marginright: '10px' }}>Source: 
       <a href="https://en.wikipedia.org/wiki/List_of_states_and_union_territories_of_India_by_population" target="_blank">en.wikipedia.org</a>
    </div>
            </div>);
    }
    onMapsLoad(args) {
        let maps = document.getElementById('maps');
        maps.setAttribute('title', '');
    }
    ;
}

render(<HeatMaps />, document.getElementById('sample'));

