import { render } from 'react-dom';
import './index.css';
/**
 * Marker cluster map sample
 */
import * as React from "react";
import { MapAjax } from '@syncfusion/ej2-maps';
import { MapsComponent, Inject, LayersDirective, LayerDirective, MarkersDirective, MarkerDirective, Marker, Zoom, MapsTooltip } from '@syncfusion/ej2-react-maps';
import { SampleBase } from './sample-base';
import * as data from './map-data/marker-cluster.json';
let datasource = data;
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
    }`;
export class MarkerCluster extends SampleBase {
    render() {
        return (<div className='control-panel'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section'>
                    <MapsComponent id="maps" load={this.load} useGroupingSeparator={true} format='n' zoomSettings={{
            enable: true
        }} titleSettings={{
            text: 'Top 50 largest cities in the World',
            textStyle: {
                size: '16px'
            }
        }}>
                        <Inject services={[Marker, MapsTooltip, Zoom]}/>
                        <LayersDirective>
                            <LayerDirective shapeData={new MapAjax('////npmci.syncfusion.com/development/react/demos/src/maps/map-data/world-map.json')} shapeSettings={{
            fill: '#C1DFF5'
        }} markerClusterSettings={{
            allowClustering: true,
            shape: 'Image',
            height: 40,
            width: 40,
            labelStyle: { color: 'white' },
            imageUrl: '////npmci.syncfusion.com/development/react/demos/src/maps/images/cluster.svg'
        }}>
                                <MarkersDirective>
                                    <MarkerDirective visible={true} dataSource={datasource.cluster} shape='Image' imageUrl='////npmci.syncfusion.com/development/react/demos/src/maps/images/ballon.png' tooltipSettings={{
            visible: true,
            valuePath: 'area',
            template: '<div id="template" style="width: 140px;opacity: 90%;background: rgba(53, 63, 76, 0.90);box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.40);padding:10px;border: 1px #abb9c6;border-radius: 4px;">' +
                '<div style="font-size:13px;color:#ffffff;font-weight: 500;"><center>${city}</center></div>' +
                '<hr style="margin-top: 2px;margin-bottom:5px;border:0.5px solid #DDDDDD">' +
                '<div><span style="font-size:13px;color:#cccccc">Rank : </span><span style="font-size:13px;color:#ffffff;font-weight: 500;">${Rank}</span></div>' +
                '<div><span style="font-size:13px;color:#cccccc">Area : </span><span style="font-size:13px;color:#ffffff;font-weight: 500;">${area} sq kms</span></div></div>',
        }} height={20} width={20} animationDuration={0}>
                                    </MarkerDirective>
                                </MarkersDirective>
                            </LayerDirective>
                        </LayersDirective>
                    </MapsComponent>
                </div>
                
                <div style={{ float: 'right', marginright: '10px' }}>Source:
       <a href="http://www.citymayors.com/statistics/largest-cities-population-125.html" target="_blank">www.citymayors.com</a>
                </div>
            </div>);
    }
}

render(<MarkerCluster />, document.getElementById('sample'));

