import { render } from 'react-dom';
import './index.css';
/**
 * OSM sample
 */
import * as React from "react";
import { Dialog } from '@syncfusion/ej2-popups';
import { enableRipple } from '@syncfusion/ej2-base';
import { MapsComponent, Inject, LayersDirective, LayerDirective, MarkersDirective, MarkerDirective, MapsTooltip, Bubble, Zoom, Marker } from '@syncfusion/ej2-react-maps';
import { SampleBase } from './sample-base';
enableRipple(true);
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
    }
    #popuposm_dialog-content {
      display: none !important
    }
    #popuposm_title {
      font-size: 14px !important
    }
    .osmpopup {
      position:relative;
      background-color:white;
    }        
    .osmpopup:after {
        content:'';
        position: absolute;
        top: 170%;
        left: 50%;
        width: 0;
        height: 0;
        margin-left: -35px;
        border-top: solid 20px white;
        border-left: solid 20px transparent;
        border-right: solid 20px transparent;
    }
    #osmdialog .e-popup{
        min-width: 100px !important
    }`;
export class OSMMaps extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section row'>
                    <div className='col-md-12'>
                        <MapsComponent id="maps" load={this.load} ref={m => this.mapInstance = m} titleSettings={{
            text: 'Headquarters of the United Nations',
            textStyle: {
                size: '16px'
            }
        }} centerPosition={{
            latitude: 40.7209,
            longitude: -73.9680
        }} zoomSettings={{
            zoomFactor: 9,
            enable: false
        }}>
                            <Inject services={[Bubble, MapsTooltip, Zoom, Marker]}/>
                            <LayersDirective>
                                <LayerDirective layerType='OSM' animationDuration={0}>
                                    <MarkersDirective>
                                    <MarkerDirective visible={true} template='<div><img src="////npmci.syncfusion.com/development/react/demos/src/maps/images/ballon.png" style="height:30px;width:20px;"></img></div>' dataSource={data1} tooltipSettings={{
            visible: true,
            valuePath: 'name'
        }}>
                                    </MarkerDirective>
                                    </MarkersDirective>
                                </LayerDirective>
                            </LayersDirective>
                        </MapsComponent>
                    </div>
                </div>
                <div style={{ float: 'right' }}>
                    <a href="https://www.openstreetmap.org/copyright" target="_blank">Â© OpenStreetMap contributors</a>
                </div>
                <br />
                
                <div style={{ float: 'right', marginright: '10px' }}>Source: 
                    <a href="https://en.wikipedia.org/wiki/Headquarters_of_the_United_Nations" target="_blank">en.wikipedia.org</a>
                </div>
            </div>);
    }
}
setTimeout(function () {
    let dialogObj = new Dialog({
        header: 'Manhattan,<br> New York, USA',
        animationSettings: { effect: 'FadeZoom' },
        showCloseIcon: true,
        height: '55px',
        width: '186px',
        target: document.getElementById('osmdialog')
    });
    dialogObj.appendTo('#popuposm');
}, 500);
export let data1 = [
    {
        name: 'Manhattan, New York, USA',
        latitude: 40.7488758,
        longitude: -73.9730091
    },
];

render(<OSMMaps />, document.getElementById('sample'));

