import { render } from 'react-dom';
import './index.css';
/**
 * OSM sample
 */
import * as React from "react";
import { MapAjax } from '@syncfusion/ej2-maps';
import { MapsComponent, Inject, LayersDirective, LayerDirective, MapsTooltip, Bubble, Zoom, AnnotationDirective, AnnotationsDirective } from '@syncfusion/ej2-react-maps';
import { SampleBase } from './sample-base';
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
    }`;
export class OSMSubLayer extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section row'>
                    <div className='col-md-12'>
                        <MapsComponent id="maps" load={this.load} ref={m => this.mapInstance = m} titleSettings={{
            text: 'Location of Africa continent in the World map',
            textStyle: {
                size: '16px'
            }
        }} zoomSettings={{
            enable: true
        }}>
                            <AnnotationsDirective>
                                <AnnotationDirective content='<div style="height:18px;width:170px;background:white;text-align:center"><a href="https://www.openstreetmap.org/copyright"  target = "_blank" > © OpenStreetMap contributors </a></div >' verticalAlignment='Far' zIndex='1' x='-40' y='-20' horizontalAlignment='Far'>
                                </AnnotationDirective>
                            </AnnotationsDirective>
                            <Inject services={[Bubble, MapsTooltip, Zoom]}/>
                            <LayersDirective>
                                <LayerDirective layerType='OSM'/>
                                <LayerDirective type='SubLayer' animationDuration={0} shapeData={new MapAjax('////npmci.syncfusion.com/development/react/demos/src/maps/map-data/africa.json')} shapeSettings={{ fill: '#5100a3', opacity: 0.4 }}>                                    
                                </LayerDirective>
                            </LayersDirective>
                        </MapsComponent>
                    </div>
                </div>
                <div style={{ float: 'right' }}> 
                    <a href="https://www.openstreetmap.org/copyright" target="_blank">© OpenStreetMap contributors</a>
                </div>
                <br />
                
                <div style={{ float: 'right', marginright: '10px' }}>Source: 
                <a href="https://www.whatarethe7continents.com/biggest-largest-smallest-continents/" target="_blank">Seven Continents</a>
                </div>
            </div>);
    }
}

render(<OSMSubLayer />, document.getElementById('sample'));

