index.css:
/**
* ej2 Menu styles
*/
@font-face {
    font-family: 'e-menu';
    src:
    url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.menu-control .em-icons { 
    font-family: 'e-menu';
    font-style: normal;
    font-variant: normal;
    font-weight: normal;
    line-height: 1;
    text-transform: none;
}

.menu-control .em-icons.e-file::before {
    content: '\e700';
}

.menu-control .em-icons.e-edit::before {
    content: '\e701';
}

.e-menu-wrapper .em-icons.e-cut::before {
    content: '\e704';
}

.e-menu-wrapper .em-icons.e-copy::before {
    content: '\e705';
}

.e-menu-wrapper .em-icons.e-paste::before {
    content: '\e706';
}

.e-menu-wrapper .em-icons.e-open::before {
    content: '\e702';
}

.e-menu-wrapper .em-icons.e-save::before {
    content: '\e703';
}

/* custom code start */
.menu-control {
    margin-top: 45px;
    text-align: center;
}
/* custom code end */

index.js:
import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { MenuComponent } from '@syncfusion/ej2-react-navigations';
import { SampleBase } from './sample-base';

/**
 * Menu default sample
 */
export class Default extends SampleBase {
    constructor() {
        super(...arguments);
        // Menu items definition
        this.menuItems = [
            {
                text: 'File',
                iconCss: 'em-icons e-file',
                items: [
                    { text: 'Open', iconCss: 'em-icons e-open' },
                    { text: 'Save', iconCss: 'em-icons e-save' },
                    { separator: true },
                    { text: 'Exit' }
                ]
            },
            {
                text: 'Edit',
                iconCss: 'em-icons e-edit',
                items: [
                    { text: 'Cut', iconCss: 'em-icons e-cut' },
                    { text: 'Copy', iconCss: 'em-icons e-copy' },
                    { text: 'Paste', iconCss: 'em-icons e-paste' }
                ]
            },
            {
                text: 'View',
                items: [
                    {
                        text: 'Toolbars',
                        items: [
                            { text: 'Menu Bar' },
                            { text: 'Bookmarks Toolbar' },
                            { text: 'Customize' },
                        ]
                    },
                    {
                        text: 'Zoom',
                        items: [
                            { text: 'Zoom In' },
                            { text: 'Zoom Out' },
                            { text: 'Reset' },
                        ]
                    },
                    { text: 'Full Screen' }
                ]
            },
            {
                text: 'Tools',
                items: [
                    { text: 'Spelling & Grammar' },
                    { text: 'Customize' },
                    { separator: true },
                    { text: 'Options' }
                ]
            },
            {
                text: 'Help'
            }
        ];
    }
    render() {
        return (<div className='control-pane'>
                <div className='control-section'>
                    <div className='menu-section'>
                        <div className='menu-control'>
                            <MenuComponent items={this.menuItems}></MenuComponent>
                        </div>
                    </div>
                </div>
            </div>);
    }
}

render(<Default />, document.getElementById('sample'));

