import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { MultiSelectComponent, CheckBoxSelection, Inject } from '@syncfusion/ej2-react-dropdowns';
import { SampleBase } from './sample-base';
import { PropertyPane } from './property-pane';
import { ButtonComponent } from '@syncfusion/ej2-react-buttons';
import { NumericTextBoxComponent } from '@syncfusion/ej2-react-inputs';

import * as data from './dataSource.json';
export class SelectionLimit extends SampleBase {
    constructor() {
        super(...arguments);
        this.temp = 'countries';
        //define the data with category
        this.countries = data[this.temp];
        // maps the appropriate column to fields property
        this.checkFields = { text: 'Name', value: 'Code' };
    }
    applyRange() {
        let value = parseFloat(document.getElementById('length').value);
        this.mulObj.value = null;
        this.mulObj.maximumSelectionLength = value;
    }
    render() {
        return (<div id="multichecbox" className='control-pane'>
        <div className='control-section col-lg-8'>
          <div id="multigroup" className="control-styles">
            <h4>Selection Limit</h4>
            <MultiSelectComponent id="checkbox" ref={(scope) => { this.mulObj = scope; }} dataSource={this.countries} fields={this.checkFields} placeholder="Select countries" mode="CheckBox" showDropDownIcon={true} maximumSelectionLength={3} filterBarPlaceholder="Search countries" popupHeight="350px">
              <Inject services={[CheckBoxSelection]}/>
            </MultiSelectComponent>
          </div>
        </div>
        <div className='col-lg-4 property-section'>
          <PropertyPane title='Properties'>
            <table id="property" title="Properties" className='property-panel-table' style={{ width: '100%' }}>
              <tr>
                <td>
                  <div>Selection Limit </div>
                </td>
                <td>
                  <div>
                    <NumericTextBoxComponent id='length' format="n0" max={this.countries.length} value={3} min={1}></NumericTextBoxComponent>
                  </div>
                </td>
              </tr>
              <tr>
                <td></td>
                <td>
                  <div>
                    <ButtonComponent id="buttonApply" cssClass='e-btn e-control e-outline' style={{ marginBottom: '10px', marginLeft: '100px' }} onClick={this.applyRange.bind(this)}>Apply</ButtonComponent>
                  </div>
                </td>
              </tr>
            </table>
          </PropertyPane>
        </div>

      </div>);
    }
}

render(<SelectionLimit />, document.getElementById('sample'));

