import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { PivotViewComponent } from '@syncfusion/ej2-react-pivotview';
import { SampleBase } from './sample-base';
import { DataManager, Query, WebApiAdaptor } from '@syncfusion/ej2-data';
/**
 * PivotView sample for Remote data source.
 */
const SAMPLE_CSS = `
.e-pivotview {
    width: 100%;
    height: 100%;
}`;
let remoteData;
let dataSourceSettings;
new DataManager({
    url: 'https://bi.syncfusion.com/northwindservice/api/orders',
    adaptor: new WebApiAdaptor,
    crossDomain: true
}).executeQuery(new Query().take(8)).then((e) => {
    remoteData = e.result;
    dataSourceSettings = {
        dataSource: remoteData,
        expandAll: true,
        filters: [],
        columns: [{ name: 'ProductName', caption: 'Product Name' }],
        rows: [{ name: 'ShipCountry', caption: 'Ship Country' }, { name: 'ShipCity', caption: 'Ship City' }],
        formatSettings: [{ name: 'UnitPrice', format: 'C0' }],
        values: [{ name: 'Quantity' }, { name: 'UnitPrice', caption: 'Unit Price' }]
    };
});
export class Remote extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section' style={{ overflow: 'auto' }}>
                    <PivotViewComponent id='PivotView' dataSourceSettings={dataSourceSettings} width={'100%'} height={'300'} gridSettings={{ columnWidth: 120 }}>
                    </PivotViewComponent>
                </div>
            </div>);
    }
}

render(<Remote />, document.getElementById('sample'));

