import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { ScheduleComponent, Day, Week, WorkWeek, Month, Agenda, Inject } from '@syncfusion/ej2-react-schedule';

import { SampleBase } from './sample-base';
import { DataManager, WebApiAdaptor } from '@syncfusion/ej2-data';
/**
 * Schedule remote data sample
 */
export class RemoteData extends SampleBase {
    constructor() {
        super(...arguments);
        this.dataManger = new DataManager({
            url: 'https://js.syncfusion.com/demos/ejservices/api/Schedule/LoadData',
            adaptor: new WebApiAdaptor,
            crossDomain: true
        });
    }
    render() {
        return (<div className='schedule-control-section'>
        <div className='control-section'>
          <div className='control-wrapper'>
            <ScheduleComponent width='100%' height='650px' selectedDate={new Date(2017, 5, 5)} eventSettings={{ dataSource: this.dataManger }} readonly={true}>
              <Inject services={[Day, Week, WorkWeek, Month, Agenda]}/>
            </ScheduleComponent>
          </div>
        </div>
      </div>);
    }
}

render(<RemoteData />, document.getElementById('sample'));

