index.css:
/* main content area styles */

.main.content {
    text-align: center;
    font-size: 20px;
    padding: 15px;
}

.textArea {
    height: 300px;
    line-height: 300px;
}
/* custom code start */
@media(max-width:550px) {
    #head .text {
        display: none;
    }
}

.center {
    text-align: center;
    display: none;
    font-size: 13px;
    font-weight: 400;
    margin-top: 20px;
}

.sb-content-tab .center {
    display: block;
}

.col-lg-12.col-sm-12.col-md-12 {
    padding: 0px;
}
/* custom code end */
/* end of main content area styles */
/* Header area styles */

#wrapper #head {
    border: none;
    background: #292961;
    width: 100%;
    height: 50px;
    color: #fff;
    padding: 0 15px;
    position: relative;
}
/* custom code start */
body {
    margin: 0px;
}
/* custom code end */
#hamburger.menu {
    font-size: 25px;
    cursor: pointer;
    float: left;
    line-height: 55px;
    position: absolute;
}

#hamburger.menu:before {
    content: '\e10d';
}

#head .text {
    text-align: right;
    position: absolute;
    height: 50px;
    line-height: 50px;
    font-size: 18px;
    padding-left: 40px;
}

#head .header {
    text-align: center;
    height: 50px;
    line-height: 50px;
    font-size: 18px;
    width: 100%
}
/* end of header area styles */
/* sidebar styles */
/* custom code start */
.sb-content-tab #wrapper {
    display: none;
}
/* custom code end */
#sidebar-menu {
    background: #f5f5f5;
    padding: 20px;
}

#wrapper #close:before {
    content: "\e109";
}

#wrapper #close {
    float: right;
    color: rgba(0, 0, 0, 0.61);
    cursor: pointer;
    font-size: 20px;
    line-height: 14px;
}
/* listview style */

#menuList.e-listview .e-list-item,
#menuList.e-listview .e-list-item:last-child {
    height: 51px;
    line-height: 51px;
    text-align: center;
    background: #f5f5f5;
    color: rgba(0, 0, 0, 0.87);
    border-bottom: none;
}

#menuList.e-listview .e-list-item:hover {
    transform: scale(1.2);
    -webkit-transition-timing-function: cubic-bezier(0.47, 2.02, 0.31, -0.36);
    transition-timing-function: cubic-bezier(0.47, 2.02, 0.31, -0.36);
}

#menuList {
    width: 200px;
    margin: 0 auto;
    position: relative;
    top: 15vh;
}
/* end of list view styles */

#sidebar-menu.e-sidebar {
    padding: 22px;
}
/* end of sidebar styles */
/* newTab support styles */

.ej2-new .sb-header,
.ej2-new .sb-bread-crumb,
.ej2-new #action-description,
.ej2-new #description-section,
.ej2-new #description {
    display: none
}

.ej2-new .container-fluid,
.ej2-new .container-fluid .control-section,
#sidebar-section {
    padding: 0px;
}

.ej2-new .sample-browser>.content.e-view {
    top: 0px;
    padding: 0px;
    text-align: initial
}

@font-face {
    font-family: 'e-icons';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}
/* end of newTab support styles */

index.js:
import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { SidebarComponent } from '@syncfusion/ej2-react-navigations';
import { ListViewComponent } from '@syncfusion/ej2-react-lists';
import { SampleBase } from './sample-base';

export class SidebarWithList extends SampleBase {
    constructor() {
        super(...arguments);
        this.dataList = [
            { text: 'Home' },
            { text: 'About' },
            { text: 'Careers' },
            { text: 'FAQs' },
            { text: 'Blog' },
            { text: 'Uses' },
            { text: 'Contact' }
        ];
        this.fields = { tooltip: 'text' };
    }
    render() {
        return (<div className="control-section">
                <div className="col-lg-12 col-sm-12 col-md-12 center">
                Click/Touch the button to view the sample
                 </div>
                <div className="col-lg-12 col-sm-12 col-md-12 center">
                    <a className="e-btn" id="newTab" target="_blank" onClick={this.newTabClick.bind(this)}>Open in new Tab</a>
                </div>
                <div id="wrapper">
                <title>Essential JS 2 for React - Sidebar > Sidebar with ListView </title>
                    <div className="col-lg-12 col-sm-12 col-md-12">
                        <div id="head">
                            <div className="text">Menu</div>
                            <span id="hamburger" className="e-icons menu" onClick={this.openClick.bind(this)}></span>
                            <div className="header">Header Content</div>
                        </div>
                        <SidebarComponent id="sidebar-menu" ref={Sidebar => this.sidebarobj = Sidebar} type="Over" width="250px">
                            <div id="close" className="e-icons" onClick={this.closeClick.bind(this)}></div>
                            <div className="content-area">
                                 
                                <ListViewComponent id="menuList" dataSource={this.dataList} fields={this.fields} select={this.onSelect.bind(this)}>
                                </ListViewComponent>
                            </div>
                        </SidebarComponent>
                        <div>
                            <div className="main content textArea">Application content</div>
                        </div>
                    </div>
                </div>
            </div>);
    }
    //open newTab
    newTabClick() {
        let URL = location.href.replace(location.search, '');
        document.getElementById('newTab').setAttribute('href', URL.split('#')[0] + 'sidebar/sidebar-list/index.html');
    }
    //close the sidebar when list item selected
    onSelect(args) {
        this.sidebarobj.hide();
        document.querySelector('.textArea').textContent = args.text + ' Page Content';
    }
    //open the sidebar
    openClick() {
        this.sidebarobj.show();
    }
    //close the sidebar
    closeClick() {
        this.sidebarobj.hide();
    }
}

render(<SidebarWithList />, document.getElementById('sample'));

