import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { SliderComponent } from '@syncfusion/ej2-react-inputs';
import { SampleBase } from './sample-base';
const slidercss = `  
.content-wrapper {
  width: 40%;
  margin: 0 auto;
  min-width: 185px;
}

.e-bigger .content-wrapper {
  width: 80%;
}

.sliderwrap label {
  padding-bottom: 26px;
  font-size: 13px;
  font-weight: 500;
  margin-top: 15px;
}

.userselect {
  -webkit-user-select: none;
  /* Safari 3.1+ */
  -moz-user-select: none;
  /* Firefox 2+ */
  -ms-user-select: none;
  /* IE 10+ */
  user-select: none;
  /* Standard syntax */
}
`;
export class Default extends SampleBase {
    render() {
        return (<div className='control-pane'>
        <div className='control-section'>
          <div className="content-wrapper">
            <style>{slidercss}</style>
            <div className='sliderwrap'>
              <label className='labeltext'>Default Slider</label>
              
              <SliderComponent value={30}/>
            </div>
            <div className='sliderwrap'>
              <label className='labeltext'>MinRange Slider</label>
              
              <SliderComponent value={30} type='MinRange'/>
            </div>
            <div className='sliderwrap'>
              <label className='labeltext'>Range Slider</label>
              
              <SliderComponent value={[30, 70]} type='Range'/>
            </div>
          </div>
        </div>


      </div>);
    }
}

render(<Default />, document.getElementById('sample'));

