index.css:
.code-editor #code-text {
    margin-left: 5px;
}
.code-editor .code-preview {
    margin-top: 15px;
    font-size: 12px;
}
.code-editor {
    margin: 20px auto;
    max-width: 600px;
}
.code-editor.control-section{            
    min-height: 370px;
    margin-bottom: 15px;
    margin-top: 10px;    
}

.code-editor .h3 {
    font-size: 14px;
    margin: 4px;
    font-weight: bold;
}

.code-editor .splitter-editor-content {
    padding: 12px;
}
.code-editor .splitter-image {
    margin: 0 auto;
    display: flex;
    height: 115px;
    margin-top: 10px;
}

#codeEditor {
    border: none;
}

div.e-pane-vertical > div:first-child {
    height: 100%;
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { SampleBase } from './sample-base';
import { SplitterComponent, PanesDirective, PaneDirective } from '@syncfusion/ej2-react-layouts';

/**
 *  Sample for code editor layout
 */
const imgStyle = {
    width: "20%",
    margin: "0 auto"
};
const paneImg = {
    margin: "auto auto 5px"
};
const lastPaneStyle = {
    padding: "auto auto 20px"
};
export class CodeEditor extends SampleBase {
    splitterInstance;
    innerSplitterInstance;
    paneSize = "53%";
    minimumSize = "30%";
    bottomPaneContent() {
        return (<div className="splitter-editor-content">
                <label className="h3">Preview of sample</label>
                <div className="splitter-image">
                    <img className="img1" src="https://ej2.syncfusion.com/demos/https://ej2.syncfusion.com/react/demos/src/listview/images/albert.png" alt="albert" style={imgStyle}/>
                </div>
            </div>);
    }
    ;
    firstPaneContent() {
        return (<div className="splitter-editor-content">
                <label className="h3">HTML</label>
                <div className="code-preview">
                    &lt;<span>!DOCTYPE html&gt;</span>
                    <div>&lt;<span>html&gt;</span></div>
                    <div>&lt;<span>body &gt;</span></div>
                    &lt;<span>div</span> id="custom-image"&gt;
        <div style={paneImg}>&lt;<span>img</span> src="https://ej2.syncfusion.com/react/demos/src/albert.png"&gt;</div>
                    <div>&lt;<span>div</span>&gt;</div>
                    <div>&lt;<span>/body&gt;</span></div>
                    <div>&lt;<span>/html&gt;</span></div>
                </div>
            </div>);
    }
    ;
    secondPaneContent() {
        return (<div className="splitter-editor-content">
                <label className="h3">CSS</label>
                <div className="code-preview">
                    <span>img &#123; </span>
                    <div id="code-text">margin:<span>0 auto;</span></div>
                    <div id="code-text">display:<span>flex;</span></div>
                    <div id="code-text">height:<span>70px;</span></div>
                    <span> &#125; </span>
                </div>
            </div>);
    }
    ;
    thirdPaneContent() {
        return (<div className="splitter-editor-content">
            <label className="h3">JavaScript</label>
                <div className="code-preview">
                    <span>var</span> image = document.getElementById("custom-image");
                    <div>image.addEventListener("click", function() &#123; </div>
                    <div style={lastPaneStyle}> Code block for click action </div>
                    <span> &#125; </span>
                </div>
            </div>);
    }
    ;
    innerSplitterElement() {
        return (<SplitterComponent id="codeEditor" ref={(splitter) => { this.innerSplitterInstance = splitter; }}>
            <PanesDirective>
                <PaneDirective size='29%' min='23%' content={this.firstPaneContent}/>
                <PaneDirective size='20%' min='15%' content={this.secondPaneContent}/>
                <PaneDirective size='35%' min='35%' content={this.thirdPaneContent}/>
            </PanesDirective>
            </SplitterComponent>);
    }
    ;
    render() {
        return (<div id="target" className="control-section code-editor">
                <SplitterComponent id="splitter2" height="400px" orientation="Vertical" ref={(splitter) => { this.splitterInstance = splitter; }}>
                    <PanesDirective>
                        <PaneDirective content={this.innerSplitterElement.bind(this)}>
                        </PaneDirective>
                        <PaneDirective size={this.paneSize} min={this.minimumSize} content={this.bottomPaneContent}>
                        </PaneDirective>
                    </PanesDirective>
                </SplitterComponent>
            </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<CodeEditor />);

