import { render } from 'react-dom';
import './index.css';
/**
 * Tooltip sample for treemap
 */
import * as React from "react";
import { TreeMapComponent, Inject, TreeMapTooltip, TreeMapLegend } from '@syncfusion/ej2-react-treemap';
import { SampleBase } from './sample-base';
import * as data from './treemap-data/airport-count.json';
let datasource = data;
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
    }`;
export class Tooltip extends SampleBase {
    render() {
        return (<div className='control-pane'>
				<style>
					{SAMPLE_CSS}
				</style>
				<div className='control-section'>
					<div className='col-md-12'>
						<TreeMapComponent load={this.load.bind(this)} id='treemap-container' tooltipSettings={{
            visible: true,
            template: '<div id="Tooltip">' +
                '<div id="displayAirports" style="border-radius: 5px;padding-left: 10px;padding-right: 10px;padding-bottom: 6px;padding-top: 6px;background: #EFEFEF;height:45px;width:150px;border: 1px #919191;box-shadow: 0px, 2px;">' +
                '<div id="airplaneicon"><img src = "////npmci.syncfusion.com/development/react/demos/src/treemap/image/airplane.svg"; style="background-repeat: no-repeat;float:left;height:32px;width:32px;"/></div>' +
                '<div id="value" style="margin-top: 5px;color: #585C60;font-family: Roboto-Bold;font-size: 16px;">' +
                '<span id="label" style="padding-left: 8px;color: #5D5D5D;font-family: Roboto-Regular;font-size: 16px;">Airports: </span><b style="margin-left: 5px">${Count}</b></div></div>' +
                '</div>'
        }} titleSettings={{
            text: 'Country wise International Airport count in South America',
            textStyle: {
                size: '15px'
            }
        }} dataSource={datasource.airport} weightValuePath='Count' equalColorValuePath='Count' legendSettings={{
            visible: true,
            position: 'Top',
            shape: 'Rectangle'
        }} leafItemSettings={{
            showLabels: true,
            labelPath: 'State',
            labelPosition: 'Center',
            labelStyle: {
                size: '13px'
            },
            fill: '#6699cc',
            colorMapping: [
                {
                    value: 25,
                    color: '#634D6F'
                },
                {
                    value: 12,
                    color: '#B34D6D'
                },
                {
                    value: 9,
                    color: '#557C5C'
                },
                {
                    value: 7,
                    color: '#44537F'
                },
                {
                    value: 6,
                    color: '#637392'
                },
                {
                    value: 3,
                    color: '#7C754D'
                },
                {
                    value: 2,
                    color: '#2E7A64'
                },
                {
                    value: 1,
                    color: '#95659A'
                },
            ]
        }}>
							<Inject services={[TreeMapTooltip, TreeMapLegend]}/>
						</TreeMapComponent>
					</div>
				</div>
				
				<div style={{ float: 'right', marginright: '10px' }}>Source:
       <a href="https://en.wikipedia.org/wiki/List_of_international_airports_by_country" target="_blank">en.wikipedia.org</a>
				</div>
			</div>);
    }
}

render(<Tooltip />, document.getElementById('sample'));

