import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { SampleBase } from './sample-base';
import { TreeViewComponent } from '@syncfusion/ej2-react-navigations';

import * as dataSource from './dataSource/multiSelect-data.json';
export class MultiSelect extends SampleBase {
    constructor() {
        super(...arguments);
        this.data = dataSource;
        this.fields = { dataSource: this.data.multiSelectData, id: 'id', parentID: 'pid', text: 'name', hasChildren: 'hasChild', selected: 'isSelected' };
        this.allowMultiSelection = true;
    }
    render() {
        return (<div className='control-pane'>
        <div className='control-section'>
          <div className='tree-control_wrapper'>
            
              <TreeViewComponent fields={this.fields} allowMultiSelection={this.allowMultiSelection}/>
          </div>
        </div>
      </div>);
    }
}

render(<MultiSelect />, document.getElementById('sample'));

